"use client";

import { usePathname } from "next/navigation";
import {
  NavigationMenu,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
} from "@/registry/default/ui/navigation-menu";

type NavLink = {
  name: string;
  url: string;
  external?: boolean;
};

const links: NavLink[] = [
  { name: "Home", url: "/" },
  { name: "Projects", url: "/projects" },
  { name: "Writings", url: "/writings" },
];

function Navbar() {
  const pathname = usePathname();

  return (
    <NavigationMenu currentPath={pathname}>
      <NavigationMenuList>
        {links.map((item) => {
          const isActive =
            item.url === "/" ? pathname === "/" : pathname.startsWith(item.url);

          return (
            <NavigationMenuItem key={item.name}>
              <NavigationMenuLink
                active={isActive}
                external={item.external}
                href={item.url}
              >
                {item.name}
              </NavigationMenuLink>
            </NavigationMenuItem>
          );
        })}
      </NavigationMenuList>
    </NavigationMenu>
  );
}

export { Navbar };
